import {Text, TextInput, View, StyleSheet, Image} from 'react-native';
import theme from '../theme/theme';
import StyledText from './StyledText';
import { Heart } from 'iconoir-react-native';

const StyledItemProduct = (props) => {
    const {} = props
    const textStyles = [
        styles.text,
        red && styles.red,
        bold && styles.bold,
        title && styles.title,
        green && styles.green,
        redBlack && styles.redBlack,
        whiteButton && styles.whiteButton
    ]
  return (
    <View style={styles.container}>

        <Image source={require("../../assets/XCampo.png")} style={styles.image}></Image>

        <View style={styles.containerText}> 
            <StyledText bold>name finca</StyledText>
            <Heart width={30} height={30} color={"black"} style={styles.icon}/>
        </View>

        <StyledText>location</StyledText>

    </View>
  );
};

const styles = StyleSheet.create(
    {
        container:{
            backgroundColor:theme.colors.greenOpacity,
            borderRadius:10,
            margin: 5,
            paddingBottom: 5
        },
        image:{
            width:180, 
            height:100, 
            borderTopLeftRadius: 10,
            borderTopRightRadius: 10
        },
        containerText:{
            flexDirection:"row", 
            justifyContent:"space-between", 
            with: 1
        },
        icon:{
            marginEnd: 5
        }
    }
)

export default StyledItemProduct;
